{
  "version": "0.2",
  "phases": {
    "build": {
      "commands": [
        "cd python-sdk-workshop/cdk_workshop/",
        "ls",
        "npm install -g aws-cdk",
        "pip install -r requirements.txt",
        "cdk synth"
      ]
    }
  },
  "artifacts": {
    "base-directory": "cdk.out",
    "files": "**/*"
  }
}
